import React from "react";
import { withFirebaseHOC } from '../firebase'
import { Link } from "react-router-dom"
import rd3 from 'react-d3-library'
import Pie from '../components/layout/navbar/Pie'


class Analysis extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      foodData: [],
      data: [
        {label: 'dogs', quantity: 140},
        {label: 'cats', quantity: 91},
        {label: 'hamsters', quantity: 88},
        {label: 'parrots', quantity: 74},
        {label: 'rabbits', quantity: 63}
      ]
    }
  }



  componentDidMount() {
    const db = this.props.firebase.db()
    db.collection('food-data').get()
      .then((snap) => {
        snap.docs.forEach((doc, i) => {
            console.log(doc.data())
          this.setState(state => ({
            foodData: [...state.foodData, {edibleTime: doc.data().edibleTime, foodType: doc.data().foodType, feedPeople: doc.data().feedPeople}]
          }))
        });
      })
      .catch(err => console.log(err))


  }



  render() {

    let width = window.innerWidth;
    let height = window.innerHeight;
    let minViewportSize = Math.min(width, height);
    // This sets the radius of the pie chart to fit within
    // the current window size, with some additional padding
    let radius = (minViewportSize * .9) / 2;
    // Centers the pie chart
    let x = width / 2;
    let y = height / 2;


      return (
        <div>
          <h1 class="display-3">Analysis of Wasted Food</h1>
          <svg width="100%" height="100%">
        {/* We'll create this component in a minute */}
        <Pie x={x} y={y} radius={radius} data={this.state.data} />
      </svg>
        </div>
      )


  }
}

export default withFirebaseHOC(Analysis)
