import React from "react";
import { withFirebaseHOC } from '../firebase'
import L from 'leaflet'
import { Map, TileLayer, Marker, Popup } from 'react-leaflet'

var myIcon = L.icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25, 41],
  iconAnchor: [12.5, 41],
  popupAnchor: [0, -41]
})

class About extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        latlng: {
          lat: 19.076,
          lng: 72.877,
        },
        zoom: 13,
        type: "",
        name: "",
        email: "",
        phone: "",
        password: "",
        hasLocation: false
    }
    this.onChange = this.onChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  onChange(e) {
      const { name, value } = e.target
      this.setState({
          [name]: value
      });
  }

  handleSubmit(e) {
      e.preventDefault()

  }



  componentDidMount() {

  }

  render() {
    const marker = this.state.hasLocation ? (
      <Marker position={this.state.latlng} icon={myIcon}>
        <Popup>
          <h2>You are here</h2>
        </Popup>
      </Marker>
    ) : null
    return (
      <div className="add-value">
          <h2>Choose</h2>
          <form onSubmit={this.handleSubmit} id="choose-form">
              <div className="form-group">
                  <select value={this.state.username} className="form-control" name="type" onChange={this.onChange} required>
                    <option value="">Choose the type</option>
                    <option value="vol" >Volunteer</option>
                    <option value="rest" >Restaurants</option>
                    <option value="shel" >Shelter</option>
                    <option value="don" >Donors</option>
                  </select>
              </div>
              <div className="form-group">
                <input type="text" id="name" name="name" className="form-control" onChange={this.onChange} value={this.state.name} autoComplete="off" placeholder="Restaurant Name" required />
              </div>
              <div className="form-group">
                <input type="text" id="email" name="email" className="form-control" onChange={this.onChange} value={this.state.email} autoComplete="off" placeholder="Email" required />
              </div>
              {/*<div className="input-field">
                <input type="password" id="password" name="password" onChange={this.onChange} value={this.state.password} autoComplete="off" placeholder="Password" required />
              </div>*/}
              <div className="form-group">
                <input type="text" id="phone" className="form-control" name="phone" onChange={this.onChange} value={this.state.phone} autoComplete="off" placeholder="Phone Number" required />
              </div>
              {/*<Map style={{height: "400px", width: "400px"}} length={4} center={this.state.latlng} onClick={this.handleClick} zoom={this.state.zoom} ref={this.mapRef}>
                <TileLayer
                  attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                {marker}
              </Map>*/}
              <button className="btn btn-primary" type="submit" name="action">Submit</button>
          </form>
      </div>
    )
  }
}

export default withFirebaseHOC(About)
